import React, {useState} from 'react';

function Info() {
  const [textCategory, setCategory] = useState('None');
  const [ text, setText ] = useState('');

  const displayText = () => {
    if (textCategory === "None"){
      setText("Some info");
    }
    else if (textCategory === "Web Crawler"){
      setText("The main way we check web security is  through a web-crawler called openwpm. This web crawler internally runs the website and then tracks the data pulls. We filter these to only show data collected from the user, instead of  images and other things.We then run it through our formula that gives out a number. Over 1000 is yellow, and over 3000 is red.");
    }
    setText("");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h3>About our Site</h3>
        <p>{text}</p>
        <ul>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); displayText(); setCategory('Web Crawler')}}>Web Crawler</button>
          </li>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); displayText(); setCategory('Cookies')}}>Cookies</button>
          </li>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); displayText(); setCategory('Ads')}}>Ads</button>
          </li>
        </ul>
      </header>
    </div>
  );
}


export default Info;
