import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';

function App() {
  const [website, setWebsite] = useState('www.google.com');

  const handleSubmit = (event) => {
    console.log(event.target.value);
    setWebsite(event.target.value);
    event.preventDefault();
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>{website}</p>

        <form onSubmit={handleSubmit}>
          <label>
            Name:
          </label>
          <input />
          <input type="submit" value="Submit" />  {/* look into using forms/inputs with useState */}
        </form>

        {/* 
        <button onClick = {() => setWebsite('www.bing.com')}>Click Me!</button>
        <button onClick = {() => setWebsite('www.yahoo.com')}>No, click me!</button> */}
      </header>
    </div>
  );
}

export default App;

