import React, {useState} from 'react';

function Info() {
  const [textCategory, setCategory] = useState('None');

  const displayText = () => {
    if (textCategory === "None"){
      return "Some info";
    }
    else if (textCategory === "Web Crawler"){
      return "Web crawler text";
    }
    return "";
  };

  return (
    <div className="App">
      <header className="App-header">
        <h3>About our Site</h3>
        <p text={displayText}></p>
        <ul>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); setCategory('Web Crawler')}}>The main way we check web security is  through a web-crawler called openwpm. This web crawler internally runs the website and then tracks the data pulls. We filter these to only show data collected from the user, instead of images and other things.We then run it through our formula
             that gives out a number. Over 1000 is yellow, and over 3000 is red.</button>
          </li>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); setCategory('Cookies')}}>Cookies</button>
          </li>
          <li>
            <button onClick={(evt) => {evt.preventDefault(); setCategory('Ads')}}>Ads</button>
          </li>
        </ul>
      </header>
    </div>
  );
}


export default Info;
