import React, {useState} from 'react';

function Info() {
  const [textCategory, setCategory] = useState('None');

  const displayText = () => {
    return textCategory;
  };

  const handleClick = (name) => {
    setCategory(name);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h3>About our Site</h3>
        <p>{displayText}</p>
        <ul>
          <li>
            <button onclick={handleClick('Web Crawler')}>Web Crawler</button>
          </li>
          <li>
            <button onclick={handleClick('Cookies')}>Cookies</button>
          </li>
          <li>
            <button onclick={handleClick('ads')}>Ads</button>
          </li>
        </ul>
      </header>
    </div>
  );
}


export default Info;
